import {
  Button,
  createTheme,
  Grid,
  Tab,
  Tabs,
  ThemeProvider,
  Typography,
} from "@mui/material";
import { makeStyles } from "@material-ui/core/styles";
import React, { useState } from "react";
import TimeIcon from "../../Atoms/TimeIcon/TimeIcon";
import ArrowForwardIcon from "@mui/icons-material/ArrowForward";
import image_2 from "../../Atoms/images/2.png";
import FinishedReading from "../../Organisms/GridOfBooks/FinishedReading";
import CurrentlyReading from "../../Organisms/GridOfBooks/CurrentlyReading";
import { Link } from "react-router-dom";

const theme = createTheme({
  palette: {
    primary: {
      main: "#2CE080",
    },
    secondary: {
      main: "#03314B",
    },
  },
});

const useStyles = makeStyles({
  Bookname: {
    position: "absolute",
    width: "496px",
    height: "45px",
    left: "264px",
    top: "206px",
    fontFamily: "Raleway",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: "36px",
    lineHeight: "45px",
    color: "#03314B",
  },
  caption: {
    position: "absolute",
    width: "509px",
    height: "25px",
    left: "264px",
    top: "275px",
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: "normal",
    fontSize: "20px",
    lineHeight: "25px",
    color: "#03314B",
  },
  author: {
    position: "absolute",
    width: "209px",
    height: "20px",
    left: "264px",
    top: "324px",
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: "normal",
    fontSize: "16px",
    lineHeight: "20px",
    color: "#6D787E",
  },
  readtime: {
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: "normal",
    fontSize: "14px",
    lineHeight: "18px",
    textAlign: "right",
    color: "#6D787E",
  },
  sendtokindle: {
    width: "110px",
    height: "20px",
    left: "0px",
    marginTop: "7px",
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: "normal",
    fontSize: "16px",
    lineHeight: "20px",
    color: "#6D787E",
  },
  image: {
    position: "absolute",
    width: "304px",
    height: "304px",
    left: "898px",
    top: "206px",
  },
  readnow: {
    position: "static",
    width: "75px",
    height: "20px",
    left: "0px",
    top: "0px",
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: 500,
    fontSize: "16px",
    lineHeight: "20px",
  },
  finishedreading: {
    position: "static",
    left: "12.35%",
    right: "12.35%",
    top: "27.27%",
    bottom: "27.27%",
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: 500,
    fontSize: "16px",
    lineHeight: "20px",
    textAlign: "center",
    color: "#03314B",
  },
  tabname: {
    position: "static",
    height: "20px",
    left: "0px",
    top: "0px",
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: "normal",
    fontSize: "16px",
    lineHeight: "20px",
    textTransform: "initial",
    "&.Mui-selected": {
      color: "#03314B",
    },
  },
  info: {
    position: "absolute",
    width: "600px",
    height: "100px",
    left: "264px",
    top: "624px",
    fontFamily: "Cera Pro",
    fontStyle: "normal",
    fontWeight: "normal",
    fontSize: "16px",
    lineHeight: "20px",
    color: "#03314B",
    marginTop: "20px",
  },
});

const ReadTime: React.FunctionComponent = () => {
  const classes = useStyles();
  return (
    <Grid container item>
      <Grid item>
        <TimeIcon />
      </Grid>
      <Grid item style={{ marginLeft: "10px" }}>
        <Typography className={classes.readtime}>15-minute read</Typography>
      </Grid>
    </Grid>
  );
};

const Info: React.FunctionComponent = () => {
  const classes = useStyles();
  return (
    <React.Fragment>
      <div>
        <Typography className={classes.info} variant="body1" fontWeight={500}>
          Beyond Entrepreneurship 2.0 (2020) updates Jim Collins and Bill
          Lazier’s essential 1992 business handbook, Beyond Entrepreneurship for
          the entrepreneurs, visionaries, and innovators of today. This new
          edition combines the timeless business advice and strategy of the
          original text, supplemented with cutting-edge insights and case
          studies pertinent to today’s business world.
        </Typography>
      </div>
    </React.Fragment>
  );
};

const Synopsis: React.FunctionComponent = () => {
  const [value, setValue] = useState(0);

  const handleChange = (
    event: React.SyntheticEvent<Element, Event>,
    newValue: number
  ) => {
    setValue(newValue);
  };

  const classes = useStyles();
  return (
    <ThemeProvider theme={theme}>
      <Tabs
        onChange={handleChange}
        value={value}
        style={{ marginTop: "72px", marginLeft: "264px" }}
      >
        <Tab
          label="Synopsis"
          className={classes.tabname}
          value={0}
          sx={{ width: "200px" }}
        />
        <Tab
          label="Who is it for?"
          className={classes.tabname}
          value={1}
          sx={{ width: "200px" }}
        />
        <Tab
          label="About the author"
          className={classes.tabname}
          value={2}
          sx={{ width: "200px" }}
        />
      </Tabs>

      {value === 0 && <Info />}
    </ThemeProvider>
  );
};
const SendToKindle: React.FunctionComponent = () => {
  const classes = useStyles();
  return (
    <Grid container item>
      <Grid item>
        <Typography className={classes.sendtokindle}>Send to Kindle</Typography>
      </Grid>

      <Grid item>
        <ArrowForwardIcon />
      </Grid>
    </Grid>
  );
};

